import React, {useEffect} from 'react';
import logo from './logo.svg';
import './App.css';

import * as d3 from 'd3';

import miserableJSON from '../src/miserables.json';

const jsondata = miserableJSON;

function App() {

  var width = 800;
  var height = 600;
  var color = d3.scaleOrdinal(d3.schemeCategory10);

    var label = {
      'nodes': [],
      'links': []
    };


useEffect(() => {
  drawChart();
});

function drawChart() {
      jsondata.nodes.forEach(function (d, i) {
      label.nodes.push({ node: d });
      label.nodes.push({ node: d });
      label.links.push({
        source: i * 2,
        target: i * 2 + 1
      });
    });

    console.log(label);
}

  return (
    <div className="App">
      {/* <svg id='viz'></svg> */}
    </div>
  );
}

export default App;
