import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';
import { lab } from 'd3';
const moment = require('moment');


const BollingerBands = (props) => {
    useEffect(() => {
        drawChart();
    });

    function drawChart() {

        var height = 600;
        var width = 900;
        var margin = ({top: 10, right: 20, bottom: 30, left: 40})

            const svg = d3.select('body').append('svg')
                .attr("width", width)
                .attr("height", height)
                .style("overflow", "visible");



                var x = d3.scaleTime()
    .domain(d3.extent(data, d => d.date))
    .rangeRound([margin.left, width - margin.right])

          var y = d3.scaleLog()
          .domain(d3.extent(values))
          .rangeRound([height - margin.bottom - 20, margin.top]);


          var xAxis = g => g
          .attr("transform", `translate(0,${height - margin.bottom})`)
          .call(d3.axisBottom(x).ticks(width / 80))
          .call(g => g.select(".domain").remove());


          var yAxis = g => g
          .attr("transform", `translate(${margin.left},0)`)
          .call(d3.axisLeft(y).tickValues(d3.ticks(...y.domain(), 10)).tickFormat(d => d))
          .call(g => g.select(".domain").remove())
          .call(g => g.selectAll(".tick line").clone()
              .attr("x2", width - margin.left - margin.right)
              .attr("stroke-opacity", 0.1))
          .call(g => g.select(".tick:last-of-type text").clone()
              .attr("x", 3)
              .attr("text-anchor", "start")
              .attr("font-weight", "bold")
              .text(data.y));



              var line = d3.line()
    .defined(d => !isNaN(d))
    .x((d, i) => x(data[i].date))
    .y(y)


            svg.append("g")
                .call(xAxis);

            svg.append("g")
                .call(yAxis);

            svg.append("g")
                .attr("fill", "none")
                .attr("stroke-width", 1.5)
                .attr("stroke-linejoin", "round")
                .attr("stroke-linecap", "round")
              .selectAll("path")
              .data([values, ...bollinger(values, N, [-K, 0, +K])])
              .join("path")
                .attr("stroke", (d, i) => ["#aaa", "green", "blue", "red"][i])
                .attr("d", line);


                function bollinger(values, N, K) {
                    let i = 0;
                    let sum = 0;
                    let sum2 = 0;
                    const bands = K.map(() => new Float64Array(values.length).fill(NaN));
                    for (let n = Math.min(N - 1, values.length); i < n; ++i) {
                      const value = values[i];
                      sum += value, sum2 += value ** 2;
                    }
                    for (let n = values.length, m = bands.length; i < n; ++i) {
                      const value = values[i];
                      sum += value, sum2 += value ** 2;
                      const mean = sum / N;
                      const deviation = Math.sqrt((sum2 - sum ** 2 / N) / (N - 1));
                      for (let j = 0; j < K.length; ++j) {
                        bands[j][i] = mean + deviation * K[j];
                      }
                      const value0 = values[i - N + 1];
                      sum -= value0, sum2 -= value0 ** 2;
                    }
                    return bands;
                  }


    }

    const d3Container = useRef(null);

    return (
        <div
            className="d3-lollipop-chart"
            width={996}
            height={444}
            ref={d3Container}
        ></div>
    );
}

export default BollingerBands;
