import React, { useRef, useEffect } from 'react';
import * as d3 from 'd3';
const moment = require('moment');


const LollipopChart = (props) => {
    useEffect(() => {
        drawChart();
    });

    function drawChart() {

        var margin = { top: 30, right: 30, bottom: 70, left: 60 },
            width = 460 - margin.left - margin.right,
            height = 400 - margin.top - margin.bottom;

        var data = [
            {
                "group": "09-2010",
                "var1": 12,
                "var2": 3
            },
            {
                "group": "01-2010",
                "var1": 23,
                "var2": 2
            },
            {
                "group": "02-2013",
                "var1": 4,
                "var2": 1
            },
            {
                "group": "04-2020",
                "var1": 8,
                "var2": 23
            },
            {
                "group": "05-2012",
                "var1": 9,
                "var2": 34
            },
            {
                "group": "01-2009",
                "var1": 10,
                "var2": 11
            },
            {
                "group": "02-2010",
                "var1": 2,
                "var2": 23
            },
            {
                "group": "08-2018",
                "var1": 3,
                "var2": 10
            },
            {
                "group": "03-2019",
                "var1": 12,
                "var2": 8
            },
            {
                "group": "04-2017",
                "var1": 22,
                "var2": 9
            }
        ];

        var empArr = [2, 4, 6, 8, 10, 12, 18, 24, 39];
        var color = d3.scaleThreshold()
            .domain(empArr)
            .range(["#bdbdbd", "#9eaeba", "#7e9fb7", "#5e90b4", "#4f88b3", "#3f80b2", "#2f79b0", "#106aad", "#0062ac"]);


        const svg = d3.select(d3Container.current).append("svg").attr('id', 'Chart').attr("width", '100%').attr("height", 500);

        svg.append("svg")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

        var x = d3.scaleBand()
            .range([0, width])
            .padding(1);
        var xAxis = svg.append("g")
            .attr("transform", "translate(0," + height + ")")

        var xAxisVar = d3.scaleBand()
            .domain(['a', 'b', 'c', 'd'])
            .range([0, width]);

        x.domain(data.map(function (d) { return d.group; }))

        svg.append("g")
            .attr('id', 'xAxisID')
            .attr("transform", "translate(0," + height + ")")
            .call(d3.axisBottom(xAxisVar).ticks(5))
            .selectAll("text")
            .attr("transform", "translate(-10,0)rotate(-45)")
            .style("text-anchor", "end");


        var y = d3.scaleLinear()
            .range([height, 0]);
        var yAxis = svg.append("g")

        y.domain([0, d3.max(data, function (d) { return +d['var1'] })]);
        yAxis.transition().duration(1000).call(d3.axisLeft(y));

        var u = svg.selectAll("circle")
            .data(data)

        u
            .enter()
            .append('g')
            .append("circle")
            .attr('fill-opacity', 0.5)
            .attr('class', function (data) { return ('circle-category circle-' + data.var1) })
            .attr('id', function (data) {
                return 'circle-' + data.id
            })

            .on('mouseover', function (d, i) {
                d3.select(this).attr("r", function (d) { return Math.round((Math.pow(d['var1'], 1)) * Math.PI / 2) + 20 })
                d3.select(this.parentNode).append('text', 'YoYoHoneySingh').style('opacity', 1)


                .attr("x", function (d) { return x(d.group) })
                .attr("y", function (d) { return y(d['var1']); })

                // .attr("x", function(d) { return x(d) - 3; })
                //     .attr("y", 100)
                    .attr("dy", ".35em")
                .text(function(d) { return d['var1'] });

            })

            .on('mouseout', function (d, i) {
                d3.select(this).attr("r", function (d) { return Math.round((Math.pow(d['var1'], 1)) * Math.PI / 2)})
                d3.select(this.parentNode).select('text').remove();
            })

            .merge(u)
            .transition()
            .duration(1000)
            .attr("cx", function (d) { return x(d.group) })
            .attr("cy", function (d) { return y(d['var1']); })
            .attr("r", function (d) { return Math.round((Math.pow(d['var1'], 1)) * Math.PI / 2) })
            .style("fill", function (d) {
                return d.var1 ? color(d.var1) : undefined;
            })
    }

    const d3Container = useRef(null);

    return (
        <svg
            className="d3-lollipop-chart"
            width={996}
            height={444}
            ref={d3Container}
        />
    );
}

export default LollipopChart;
